+// Reset CSS 
+
+*, *:before, *:after{
+   box-sizing: border-box;
+}
+
+html, body, div, span, object, iframe, figure, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, code, em, img, small, strike, strong, sub, sup, tt, b, u, i, ol, ul, li, fieldset, form, label, table, caption, tbody, tfoot, thead, tr, th, td, main, canvas, embed, footer, header, nav, section, video{
+   margin: 0;
+   padding: 0;
+   border: 0;
+   font-size: 100%;
+   font: inherit;
+   vertical-align: baseline;
+   text-rendering: optimizeLegibility;
+   -webkit-font-smoothing: antialiased;
+   text-size-adjust: none;
+}
+
+footer, header, nav, section, main{
+   display: block;
+}
+
+body{
+   line-height: 1;
+}
+
+ol, ul{
+   list-style: none;
+}
+
+blockquote, q{
+   quotes: none;
+}
+
+blockquote:before, blockquote:after, q:before, q:after{
+   content: '';
+   content: none;
+}
+
+table{
+   border-collapse: collapse;
+   border-spacing: 0;
+}
+
+input{
+   -webkit-appearance: none;
+   border-radius: 0;
+}
+
+// Reset CSS 
+
+.background{
+   background-image: url('../assets//main-background_desktop.jpg');
+   background-repeat: no-repeat;
+   // background-position: center center;
+   // background-attachment: fixed;
+   background-size: cover;
+   // height: 100vh;
+}
+
+.content{
+   // height: 95vh;
+}
+
+.footer{
+   background-image: url('../assets/footer-background.jpg');
+   background-repeat: no-repeat;
+   background-size: cover;
+   width: auto;
+   height: 200px;
+}
+
+.logo{
+ display: block;
+ text-align: center;
+ 
+   img{
+      height: 9vw;
+      max-width: 100%;
+      object-fit: contain;
+      margin-top: 6%;
+   }
+}
+
+.main-offer_text{
+   text-align: right;
+   img{
+   height: 15vw;
+   max-width: 100%;
+   object-fit: contain;
+   }
+}
+
+.main-offer_reel{
+   position: relative;
+   top: 0;
+   left: 0;
+   // z-index: 1;
+   .spin-machine{
+      // margin-top: -20px;
+      margin: -5% 0 0 100px;
+      height: 22vw;
+      max-width: 100%;
+      object-fit: contain; 
+      -ms-transform: rotate(15deg); /* IE 9 */
+      transform: rotate(15deg);
+
+
+
+      position: relative;
+      top: 0;
+      left: 0;
+  
+    z-index: 1;
+      
+      // z-index: 1;
+   }
+   .spin-machine_prize{
+      // transform: rotate(1deg);
+      // position: absolute;
+      max-width: 100%;
+    height: 42vw;
+   //  position: absolute;
+   //  left: 9%;
+   //    -ms-transform: rotate(15deg); /* IE 9 */
+   //    transform: rotate(15deg);
+      // position: relative;
+      // left: -50%;
+      // overflow: hidden;
+      // z-index: -0;
+
+      // position: absolute;
+      top: 30px;
+      left: 30px;
+      z-index: 999;
+   }
+
+}
+
+
+
+
+.Animated-slot{
+   position: absolute;
+}
+.outer {
+   // background-color: red;
+   -ms-transform: rotate(15deg); /* IE 9 */
+      transform: rotate(15deg);
+      height: 10vw;
+   overflow: hidden;
+   position: absolute;
+   top: 45%;
+   left: 31%;
+}
+
+.inner {
+   // display: inline-block;
+   // position: relative;
+   // right: -50%;
+}
+
+@font-face {
+   font-family:"Komika";
+   src: url("../assets/komika.ttf") format("truetype");
+ }
+
+.join-now{
+   button{
+      font-family:'Komika', Courier, monospace;
+      // line-height: 50px;
+
+      color: rgb(160, 65, 7);
+      font-size: 2em;
+      border-radius: 15px;
+      height: 2em;
+      width: 260px;
+      text-shadow: 2px 2px rgb(249, 204, 114);
+      // display: block;
+      // margin: auto;
+      text-align: center;
+
+      display: table-cell; 
+      vertical-align: middle;
+      padding-bottom: 10px;
+      
+      margin-bottom: 200px;
+      // background: linear-gradient(0deg, rgb(247, 196, 46) 50%, rgb(255, 232, 89) 50%),linear-gradient(rgb(187, 25, 25), rgb(9, 233, 102)) no-repeat border-box;
+      background: linear-gradient(0deg, rgb(247, 196, 46) 50%, rgb(255, 232, 89) 50%),linear-gradient(rgb(187, 25, 25), rgb(9, 233, 102)) no-repeat border-box;
+      // background: linear-gradient(90deg, rgb(25, 2, 89) 50%, rgb(255, 89, 139) 50%);
+      border: none;
+
+      // box-shadow: inset 0 0 30px rgb(244, 145, 32);
+      // box-shadow: inset 0px 0px 60px  rgb(21, 14, 5);
+   }
+
+}
+.slot {
+   // width: auto;
+   // height: 100%;
+   // overflow: hidden;
+   // float: left;
+   // margin-right: 2vh;
+ }
+ 
+ .slot .slot-inner .panel {
+   background: red;
+   width: 20vh;
+   height: 20vh;
+   text-align: center;
+   margin-bottom: 2vh;
+   color: #fff;
+   font-family: Courier New;
+   line-height: 20vh;
+   font-size: 10vh;
+ }
+ 
+ .slot.first .slot-inner {
+   animation: spin;
+   animation-duration: 10s;
+   animation-iteration-count: infinite;
+   animation-timing-function: linear;
+   // animation-play-state: paused;
+   // animation-delay: 2s;
+ }
+ 
+ @keyframes spin {
+   // from { margin-top: 0; }
+   // to { margin-top: -174.6vh; }
+
+   // from { margin-top: -176; }
+   // to { margin-top: -10vh; }
+
+   0%   {margin-top: 0;}
+   40%  {margin-top: -140vh;}
+   // 75% {margin-top:-100vh;}
+   100% {margin-top: -140vh;}
+   
+ }
+
+

